import React from 'react'
import './App.css';
import {LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend} from 'recharts';


class Plot extends React.Component {
  constructor() {
    super(props)
    var prediction_date = "" // use this to change line color on plot, will need to get it as a percentage of total dates
      for (var i=0; i<this.props.data.length; i++) {
            if (this.props.data[i].Actual == "Prediction") {
                  prediction_date = this.props.data[i].Date
                  break
            }
      }
      var line_percent = i*100/(this.props.data.length) // change color of line at this percentage
  }

  render() {
    return (<div className = "output">
                  <div className="plot-container">
                    <div className="plot-legend">
                      <svg width="150" height="10">
                        <circle cx="111" cy="7" r="2" stroke="#0d47a1" fill="#0d47a1" />
                        <text x="114" y="7" class="small">Actual</text>
                        <circle cx="132" cy="7" r="2" stroke="#eb3710" fill="#eb3710" />
                        <text x="135" y="7" class="small">Predicted</text>
                      </svg>
                    </div>
                    <LineChart
                      width = {150}
                      height = {60}
                      data = {this.props.data}
                      margin = {{ top: 5, right: 20, left: 10, bottom: 5 }}
                    >
                      <defs>
                        <linearGradient id="linecolor" x1="0%" y1="0" x2="100%" y2="0">
                          <stop offset="0%" stopColor="#0d47a1" />
                          <stop offset={`${line_percent}%`} stopColor="#0d47a1"/>
                          <stop offset={`${line_percent}%`} stopColor="#eb3710"/>
                          <stop offset="100%" stopColor="#eb3710"/>
                        </linearGradient>
                      </defs>
                      <YAxis domain={[0, 40000]}/>
                      <XAxis dataKey="Date" />
                      <Tooltip />
                      <CartesianGrid stroke="#f5f5f5" />
                      <Line
                        type="monotone"
                        dataKey="CumFatality"
                        stroke="url(#linecolor)"
                        yAxisId={0}
                        dot={false}
                        strokeWidth={4}
                      />
                    </LineChart>
                  </div>
              </div>)
      }
}

export default Plot
